import { useEffect, useState } from "react";
import axios from 'axios';

export default function ListeTache() {
    const donnees = [
      { nom: 'Tâche 1', description: 'Tâche que je dois faire rapidement', statut: 'Fini', date: '2025-05-01' },
      { nom: 'Tâche 2', description: 'Tâchevolontairement très long pour tester le comportement du tableau.', statut: 'Non Fini', date: '2025-05-03' },
      { nom: 'Tâche 3', description: 'Encore une tâche', statut: 'En cours', date: '2025-05-05' },
      { nom: 'Tâche 4', description: 'Simple tâche à exécuter', statut: 'Fini', date: '2025-05-07' },
    ];

    const [Taches, setTaches] = useState('');

    const AffichageTache = async () =>
    {
        const res = await axios.get('http://localhost:5000/tache');
        setTaches(res.data);
    }
    useEffect(()=>
    {
        AffichageTache();
    });

    return (
      <div className='w-full h-screen flex transition-all duration-300'>
        <div className='flex flex-col w-full transition-all duration-300 h-screen'>

          <div className="w-full h-20 font-bold text-3xl border-b shadow-md flex items-center justify-center">
            <span className="text-center">Listes des tâches</span>
            <span className="absolute right-10"><i className="fas fa-plus border rounded-full p-3 border-black hover:bg-gray-800 hover:text-white hover:border-white transition-all duration-200 cursor-pointer"></i></span>
          </div>

          <div className="p-6 w-full overflow-y-auto">
            <div className="rounded-lg shadow-md">
              <table className="min-w-full text-sm text-left text-gray-200 bg-gray-800 border border-gray-700 table-fixed">
                <thead className="text-xs uppercase bg-gray-800 text-gray-300">
                  <tr>
                    <th className="px-4 py-3 text-xl text-center w-[15%]">Nom</th>
                    <th className="px-4 py-3 text-xl text-center w-[45%]">Description</th>
                    <th className="px-4 py-3 text-xl text-center w-[20%]">Date</th>
                    <th className="px-4 py-3 text-xl text-center w-[20%]">Statut</th>
                  </tr>
                </thead>
                <tbody>
                  {Taches.map((tache, index) => (
                    <tr key={index} className={`${index % 2 === 0 ? 'bg-gray-900' : 'bg-gray-800'}`}>
                      <td className="px-4 py-4 font-medium text-white w-[15%] break-words whitespace-normal text-center">{tache.nom}</td>
                      <td className="px-4 py-4 w-[45%] break-words whitespace-normal">{tache.description}</td>
                      <td className="px-4 py-4 w-[20%] break-words whitespace-normal text-center">{tache.date}</td>
                      <td className="px-4 py-4 w-[20%] break-words whitespace-normal text-center">{tache.statut}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

        </div>
      </div>
    );
  }
