import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import axios from 'axios';

export default function ListeTache() {
  const [Taches, setTaches] = useState([]);

  const AffichageTache = async () => {
    try {
      const res = await axios.get('http://localhost:5000/tache');
      setTaches(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  const ToggleStatus = async (id, currentStatus) => {
    try {
      await axios.put(`http://localhost:5000/tache/status/${id}`, {
        status: currentStatus === 1 ? 0 : 1,
      });
      AffichageTache(); // refresh after update
    } catch (error) {
      console.error('Erreur lors du changement de statut', error);
    }
  };

  useEffect(() => {
    AffichageTache();
  }, []);

  return (
    <div className='flex w-full h-screen transition-all duration-300'>
      <div className='flex flex-col w-full h-screen'>
        <div className="flex items-center justify-center w-full h-20 text-3xl font-bold border-b shadow-md">
          <span className="text-center">Listes des tâches</span>
          <span className="absolute right-10">
            <Link to='/Ajouter' className='text-black'>
              <i className="p-3 border border-black rounded-full fas fa-plus hover:bg-gray-800 hover:text-white hover:border-white"></i>
            </Link>
          </span>
        </div>

        <div className="w-full p-6 overflow-y-auto">
          <div className="rounded-lg shadow-md">
            <table className="min-w-full text-sm text-left text-gray-200 bg-gray-800 border border-gray-700 table-fixed">
              <thead className="text-xs text-gray-300 uppercase bg-gray-800">
                <tr className="border-b border-white border-double">
                  <th className="px-5 py-3 text-xl text-center w-[15%]">Nom</th>
                  <th className="px-5 py-3 text-xl w-[40%]">Description</th>
                  <th className="px-5 py-3 text-xl text-center w-[20%]">Date</th>
                  <th className="px-5 py-3 text-xl text-center w-[10%]">Statut</th>
                  <th className="px-5 py-3 text-xl text-center w-[15%]">Action</th>
                </tr>
              </thead>
              <tbody>
                {Taches.map((tache) => (
                  <tr key={tache.id} className={`border-b ${tache.id % 2 === 0 ? 'bg-gray-900' : 'bg-gray-800'}`}>
                    <td className="px-4 py-4 text-center break-words">{tache.nom}</td>
                    <td className="px-4 py-4 break-words">{tache.description}</td>
                    <td className="px-4 py-4 text-center">{new Date(tache.date).toLocaleDateString("fr-FR")}</td>
                    <td className="px-4 py-4 text-center">
                      {tache.status === 1 ? (
                        <i className="text-green-500 fas fa-check"></i>
                      ) : (
                        <i className="text-red-500 fas fa-xmark"></i>
                      )}
                    </td>
                    <td className="px-4 py-4 text-center">
                      <i
                        onClick={() => ToggleStatus(tache.id, tache.status)}
                        className="px-2 py-1 text-lg text-white transition bg-blue-600 rounded hover:bg-blue-700 fas fa-arrow-right-arrow-left"
                      >

                      </i>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}
