import { useState } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';

export default function AjoutTache () {
  const [nom, setNom] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [alertType, setAlertType] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/tache", {
        nom: nom,
        description: description
      });
      setMessage("Tâche ajoutée avec succès !");
      setAlertType("success");
      setNom('');
      setDescription('');
    } catch (error) {
      console.error("Erreur lors de l'ajout :", error);
      setMessage("Une erreur est survenue.");
      setAlertType("danger");
    }
  }

  return (
    <div className='flex w-full h-screen transition-all duration-300'>
      <div className='flex flex-col w-full transition-all duration-300'>
        <div className="relative flex items-center justify-center w-full h-20 text-3xl font-bold text-center border-b shadow-md">Ajouter des tâches</div>
            {/* Bootstrap Alert */}
            {message && (
              <div className={` text-center mx-10 alert alert-${alertType} mt-4`} role="alert">
                {message}<div className=""><Link to='/Liste'>ici</Link></div>
                <span className="absolute right-5"><div className="btn-close" data-bs-dismiss="alert"></div></span>
              </div>
            )}

        <div className="flex items-center justify-center w-full h-full">
          <div className="w-[40%] h-[70%] bg-gray-800 rounded-lg text-white p-4">
            <div className="p-3 text-lg text-center border-b border-white">Une nouvelle tâche</div>


            <div className="h-[90%] w-full flex items-center p-5 mt-10">
              <form className="flex flex-col w-full mb-32" onSubmit={handleSubmit}>
                <label className="py-4 text-lg font-bold">Entrez le titre de la nouvelle tâche :</label>
                <input type="text" className="p-3 text-white input" placeholder="Titre" value={nom} onChange={(e) => setNom(e.target.value)} required />
                <label className="py-4 text-lg font-bold">Entrez les descriptions liées à cette tâche :</label>
                <input type="text" className="p-3 text-white input" placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} required />
                <input type="submit" className="p-3 mt-20 bg-gray-600 rounded-lg hover:bg-gray-500" value='Valider' />
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
