import { useState } from 'react'
function Sidebars () {
  // Etat
  const [SiOuvert, setSiOuvert] = useState(true)
  return (
    <div
      className={`flex flex-col h-screen bg-blue-200 relative transition-all duration-300 ${
        SiOuvert ? 'w-[20%]' : 'w-[5%]'
      }`}>

        <div>
            <div className={`items-start text-center p-3 transition-all duration-300 ${
            SiOuvert ? 'text-3xl' : 'text-xl'
            }`}
            onClick={()=>setSiOuvert(true)}
            >
                {SiOuvert ? <div className='font-bold underline underline-offset-4'>Menu</div> : <i className="fas fa-bars text-5xl cursor-pointer"></i>}
                <hr />
            </div>
            <span className='absolute right-3 top-5' onClick={() => setSiOuvert(false)}>{SiOuvert && <i className='cursor-pointer fas fa-angle-left text-3xl'></i>}</span>
        </div>

        <div className="items-center h-full flex justify-center w-full flex-col">
            <div className="p-3 mb-4 rounded-lg cursor-pointer w-full text-center hover:bg-blue-300 relative text-xl"><i className={`fas fa-user text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span className='transition-all duration-700'>Dashboard</span>}</div>
            <div className="p-3 mb-4 rounded-lg cursor-pointer w-full text-center hover:bg-blue-300 relative text-xl"><i className={`fas fa-user text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Ajouter des tâches</span>}</div>
            <div className="p-3 mb-4 rounded-lg cursor-pointer w-full text-center hover:bg-blue-300 relative text-xl"><i className={`fas fa-user text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Listes des tâches</span>}</div>
            <div className="p-3 mb-4 rounded-lg cursor-pointer w-full text-center hover:bg-blue-300 relative text-xl"><i className={`fas fa-user text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Tâche non fini</span>}</div>
            <div className="p-3 mb-4 rounded-lg cursor-pointer w-full text-center hover:bg-blue-300 relative text-xl"><i className={`fas fa-user text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Tâche fini</span>}</div>
        </div>

        <div className="items-end justify-center">
            <div className="p-3 mb-1 rounded-lg cursor-pointer w-full text-center hover:bg-blue-300 relative text-xl"><i className={`fas fa-user text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Quitter</span>}</div>
        </div>



    </div>
  )
}
export default Sidebars
