import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import axios from 'axios';

export default function ListeTache() {
  const dateBrute = "2025-05-06T21:00:00.000Z";
  const dateFormatee = new Date(dateBrute).toLocaleDateString("fr-FR");
  // Résultat : "06/05/2025"

    const [Taches, setTaches] = useState([]);

    const AffichageTache = async () =>
    {
        try 
        {
            const res = await axios.get('http://localhost:5000/tache');
            setTaches(res.data);
        } 
        catch (error) 
        {
            console.error(error)    
        }
    }
    useEffect(()=>
    {
        AffichageTache();
    },[]);

    return (
      <div className='flex w-full h-screen transition-all duration-300'>
        <div className='flex flex-col w-full h-screen transition-all duration-300'>

          <div className="flex items-center justify-center w-full h-20 text-3xl font-bold border-b shadow-md">
            <span className="text-center">Listes des tâches</span>
            <span className="absolute right-10"><Link to='/Ajouter' className='text-black'><i className="p-3 transition-all duration-200 border border-black rounded-full cursor-pointer fas fa-plus hover:bg-gray-800 hover:text-white hover:border-white"></i></Link></span>
          </div>

          <div className="w-full p-6 overflow-y-auto">
            <div className="rounded-lg shadow-md">
              <table className="min-w-full text-sm text-left text-gray-200 bg-gray-800 border border-gray-700 table-fixed">
                <thead className="text-xs text-gray-300 uppercase bg-gray-800">
                  <tr className="border-b border-white border-double">
                    <th className="px-5 py-3 text-xl text-center w-[15%]">Nom</th>
                    <th className="px-5 py-3 text-xl w-[45%]">Description</th>
                    <th className="px-5 py-3 text-xl text-center w-[20%]">Date</th>
                    <th className="px-5 py-3 text-xl text-center w-[20%]">Statut</th>
                  </tr>
                </thead>
                <tbody>
                  {Taches.map((tache) => (
                    <tr key={tache.id} className={`cursor-pointer border-b hover:border-dashed hover:border-white ${tache.id % 2 === 0 ? 'bg-gray-900' : 'bg-gray-800'}`}>
                      <td className="px-4 py-4 font-medium text-white w-[15%] break-words whitespace-normal text-center">{tache.nom}</td>
                      <td className="px-4 py-4 w-[45%] break-words whitespace-normal">{tache.description}</td>
                      <td className="px-4 py-4 w-[20%] break-words whitespace-normal text-center">{new Date(tache.date).toLocaleDateString("fr-FR")}</td>
                      <td className="px-4 py-4 w-[20%] break-words whitespace-normal text-center">{tache.status ===1 ? (<i className="text-green-500 fas fa-check"></i>) : (<i className="text-red-500 fas fa-xmark"></i>)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

        </div>
      </div>
    );
  }
