import { Link } from 'react-router-dom';
import { useState } from 'react'
function Sidebars () {
  // Etat
  const [SiOuvert, setSiOuvert] = useState(false)
  return (
    <div
      className={`flex flex-col h-screen bg-gray-900 text-white relative transition-all duration-300 shadow ${
        SiOuvert ? 'w-[20%]' : 'w-[5%]'
      }`}>

        <div>
            <div className={`items-start text-center p-3 transition-all duration-300 ${
            SiOuvert ? 'text-3xl' : 'text-xl'
            }`}
            onClick={()=>setSiOuvert(true)}
            >
                {SiOuvert ? <div className='font-bold underline underline-offset-4'>Menu</div> : <i className="fas fa-bars text-5xl cursor-pointer"></i>}
                <hr />
            </div>
            <span className='absolute right-3 top-5' onClick={() => setSiOuvert(false)}>{SiOuvert && <i className='cursor-pointer fas fa-angle-left text-3xl'></i>}</span>
        </div>

        <div className="items-center h-full flex justify-center w-full flex-col">
            <Link to='/' className="p-3 mb-4 rounded-lg cursor-pointer w-full text-center hover:bg-gray-700 relative text-xl text-white no-underline"><div><i className={`fas fa-chart-line text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Dashboard</span>}</div></Link>
            <Link to='/Ajouter' className="p-3 mb-4 rounded-lg cursor-pointer w-full text-center hover:bg-gray-700 relative text-xl text-white no-underline"><div><i className={`fas fa-folder-plus text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Ajouter des tâches</span>}</div></Link>
            <Link to='/Liste' className="p-3 mb-4 rounded-lg cursor-pointer w-full text-center hover:bg-gray-700 relative text-xl text-white no-underline"><div><i className={`fas fa-list-check text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Listes des tâches</span>}</div></Link>
            <Link to='/Fini' className="p-3 mb-4 rounded-lg cursor-pointer w-full text-center hover:bg-gray-700 relative text-xl text-white no-underline"><div><i className={`fas fa-clipboard text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Tâche fini</span>}</div></Link>
            <Link to='/NonFini' className="p-3 mb-4 rounded-lg cursor-pointer w-full text-center hover:bg-gray-700 relative text-xl text-white no-underline"><div><i className={`fas fa-clipboard-question text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Tâche non fini</span>}</div></Link>
        </div>

        <div className="items-end justify-center">
            <div className="p-3 mb-1 rounded-lg cursor-pointer w-full text-center hover:bg-gray-700 relative text-xl"><i className={`fas fa-arrow-right-to-bracket text-2xl ${SiOuvert ? 'absolute left-4' : 'text-center'}`}></i> {SiOuvert && <span>Quitter</span>}</div>
        </div>



    </div>
  )
}
export default Sidebars
