import { useState } from "react";
function Sidebars()
{
    // Etat
    const [SiOuvert, setSiOuvert] = useState(true);
    return(
        <div
        className={`bg-blue-200 h-full cursor-pointer transition-all duration-300 ${
            SiOuvert ? "w-[20%]" : "w-[5%]"
        }`}
        onClick={() => setSiOuvert(!SiOuvert)}
      >
        {SiOuvert ? "Sidebar (Cliquez pour réduire)" : ">>"}
      </div>
    )
}
export default Sidebars;
