import { useEffect, useState } from "react";
import axios from "axios";

export default function Index () {
  const [taches, setTaches] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/tache")
      .then(res => setTaches(res.data))
      .catch(err => console.error("Erreur lors du chargement des tâches", err));
  }, []);

  const total = taches.length;
  const terminees = taches.filter(t => t.status === 1).length;
  const nonTerminees = taches.filter(t => t.status === 0).length;

  return (
    <div className='flex w-full h-screen overflow-hidden transition-all duration-300'>
      <div className='flex flex-col w-full h-screen transition-all duration-300'>
        <div className="relative flex items-center justify-center w-full h-20 text-3xl font-bold text-center border-b shadow-md">
          Dashboard
        </div>

        <div className="grid w-full h-full grid-cols-2 gap-6 p-10">
          {/* Toutes les tâches */}
          <div className="p-4 text-white bg-gray-800 border rounded-lg shadow-md">
            <div className="p-3 text-lg text-center border-b border-white">Toutes les tâches ({total})</div>
            <ul className="p-3 list-disc list-inside">
              {taches.map((t, i) => (
                <li key={i}>{t.nom}</li>
              ))}
            </ul>
          </div>

          {/* Tâches terminées */}
          <div className="p-4 text-white bg-gray-800 border rounded-lg shadow-md">
            <div className="p-3 text-lg text-center border-b border-white">Tâches finies ({terminees})</div>
            <ul className="p-3 list-disc list-inside">
              {taches.filter(t => t.status === 1).map((t, i) => (
                <li key={i}>{t.nom}</li>
              ))}
            </ul>
          </div>

          {/* Tâches non terminées */}
          <div className="p-4 text-white bg-gray-800 border rounded-lg shadow-md">
            <div className="p-3 text-lg text-center border-b border-white">Tâches non finies ({nonTerminees})</div>
            <ul className="p-3 list-disc list-inside">
              {taches.filter(t => t.status === 0).map((t, i) => (
                <li key={i}>{t.nom}</li>
              ))}
            </ul>
          </div>

          {/* Résumé */}
          <div className="flex flex-col items-center justify-center p-4 text-white bg-gray-800 border rounded-lg shadow-md">
            <div className="w-full p-3 text-lg text-center border-b border-white">Résumé</div>
            <div className="p-6 text-center">
              <p className="mb-2 text-2xl">Total : {total}</p>
              <p className="text-green-400">✔️ Finies : {terminees}</p>
              <p className="text-red-400">❌ Non finies : {nonTerminees}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
