import { useState } from 'react'
function Sidebars () {
  // Etat
  const [SiOuvert, setSiOuvert] = useState(true)
  return (
    <div
      className={`flex flex-col h-screen bg-blue-200 relative transition-all duration-300 ${
        SiOuvert ? 'w-[20%]' : 'w-[5%]'
      }`}>

        <div>
            <div className={`items-start text-center underline underline-offset-4 p-3 transition-all duration-300 ${
            SiOuvert ? 'text-3xl' : 'text-xl'
            }`}
            onClick={()=>setSiOuvert(true)}
            >
                {SiOuvert ? <div className=''>Menu</div> : <i className="fas fa-bars p-2 text-5xl cursor-pointer"></i>}
                <hr />
            </div>
            <span className='absolute right-3 top-5' onClick={() => setSiOuvert(false)}>{SiOuvert && <i className='cursor-pointer fas fa-angle-left text-3xl'></i>}</span>
        </div>

        <div className="items-center h-full flex justify-center w-full flex-col">
            <div className="p-3 border w-full text-center hover:bg-red-400">Menu 1</div>
            <div className="p-3 border w-full text-center hover:bg-red-400">Menu 2</div>
            <div className="p-3 border w-full text-center hover:bg-red-400">Menu 3</div>
            <div className="p-3 border w-full text-center hover:bg-red-400">Menu 4</div>
            <div className="p-3 border w-full text-center hover:bg-red-400">Menu 5</div>
        </div>

        <div className=""></div>



    </div>
  )
}
export default Sidebars
