import { useEffect, useState } from "react";
import axios from "axios";

export default function Index() {
  const [taches, setTaches] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/tache")
      .then(res => setTaches(res.data))
      .catch(err => console.error("Erreur lors du chargement des tÃ¢ches", err));
  }, []);

  const tachesTerminees = taches.filter(t => t.status === 1);
  const tachesNonTerminees = taches.filter(t => t.status === 0);


  const renderTable = (Taches) => (
    <div className="flex-1 overflow-x-hidden overflow-y-auto">
      <table className="min-w-full text-sm text-left text-gray-200 bg-gray-800 border border-gray-700 table-fixed">
        <thead className="text-xs text-gray-300 uppercase bg-gray-800">
          <tr className="fixed border-b border-white border-double">
            <th className="px-4 py-3 text-center w-[15%]">Nom</th>
            <th className="px-4 py-3 w-[40%]">Description</th>
            <th className="px-4 py-3 text-center w-[20%]">Date</th>
            <th className="px-4 py-3 text-center w-[10%]">Statut</th>
          </tr>
        </thead>
        <tbody>
          {Taches.map((tache) => (
            <tr key={tache.id} className={`border-b ${tache.id % 2 === 0 ? 'bg-gray-900' : 'bg-gray-800'}`}>
              <td className="px-3 py-3 text-center break-words">{tache.nom}</td>
              <td className="px-3 py-3 break-words">{tache.description}</td>
              <td className="px-3 py-3 text-center">
                {new Date(tache.date).toLocaleDateString("fr-FR")}
              </td>
              <td className="px-3 py-3 text-center">
                {tache.status === 1 ? (
                  <i className="text-green-500 fas fa-check"></i>
                ) : (
                  <i className="text-red-500 fas fa-xmark"></i>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className='flex w-full h-screen overflow-hidden transition-all duration-300'>
      <div className='flex flex-col w-full h-screen transition-all duration-300'>

        <div className="relative flex items-center justify-center w-full h-20 text-2xl font-bold text-center border-b shadow-md">
          Dashboard
        </div>

        <div className="grid w-full h-full grid-cols-1 gap-5 px-5 py-4 overflow-auto md:grid-cols-2">

          {/* Liste des tÃ¢ches */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white h-[41vh] border p-4 flex flex-col">
            <div className="pb-2 text-base font-semibold text-center border-b border-white">
              Liste des tÃ¢ches ({taches.length})
            </div>
            {renderTable(taches)}
          </div>

          {/* TÃ¢ches terminÃ©es */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white h-[41vh] border p-4 flex flex-col">
            <div className="pb-2 text-base font-semibold text-center border-b border-white">
              TÃ¢ches terminÃ©es ({tachesTerminees.length})
            </div>
            {renderTable(tachesTerminees)}
          </div>

          {/* TÃ¢ches non terminÃ©es */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white h-[41vh] border p-4 flex flex-col">
            <div className="pb-2 text-base font-semibold text-center border-b border-white">
              TÃ¢ches non terminÃ©es ({tachesNonTerminees.length})
            </div>
            {renderTable(tachesNonTerminees)}
          </div>

          {/* RÃ©sumÃ© */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white h-[41vh] border flex flex-col justify-center items-center p-4">
            <div className="w-full pb-2 text-base font-semibold text-center border-b border-white">
              RÃ©sumÃ©s
            </div>
            <div className="p-6 mt-4 text-lg text-center">
              <p>Total : {taches.length}</p>
              <p className="mt-2 text-green-400">âï¸ TerminÃ©es : {tachesTerminees.length}</p>
              <p className="mt-2 text-red-400">â Non terminÃ©es : {tachesNonTerminees.length}</p>
            </div>
          </div>

        </div>
      </div>
    </div>
  );
}
