import { useEffect, useState } from "react";
import axios from "axios";

export default function Index() {
  const [taches, setTaches] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/tache")
      .then(res => setTaches(res.data))
      .catch(err => console.error("Erreur lors du chargement des tâches", err));
  }, []);

  const tachesTerminees = taches.filter(t => t.status === 1);
  const tachesNonTerminees = taches.filter(t => t.status === 0);

  return (
    <div className='flex w-full h-screen overflow-hidden transition-all duration-300'>
      <div className='flex flex-col w-full h-screen transition-all duration-300'>

        <div className="relative flex items-center justify-center w-full h-20 text-3xl font-bold text-center border-b shadow-md">
          Dashboard
        </div>

        <div className="grid w-full h-full grid-cols-2 gap-10 ml-10 mt-7">

          {/* Liste des tâches */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white w-[85%] h-[90%] border">
            <div className="p-3 text-lg text-center border-b border-white">Liste des tâches ({taches.length})</div>
            <ul className="p-3 list-disc list-inside overflow-y-auto max-h-[85%]">
              {taches.map((t, i) => (
                <li key={i}>{t.nom}</li>
              ))}
            </ul>
          </div>

          {/* Tâches terminées */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white w-[85%] h-[90%] border">
            <div className="p-3 text-lg text-center border-b border-white">Tâches terminées ({tachesTerminees.length})</div>
            <ul className="p-3 list-disc list-inside overflow-y-auto max-h-[85%]">
              {tachesTerminees.map((t, i) => (
                <li key={i}>{t.nom}</li>
              ))}
            </ul>
          </div>

          {/* Tâches non terminées */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white w-[85%] h-[90%] border">
            <div className="p-3 text-lg text-center border-b border-white">Tâches non terminées ({tachesNonTerminees.length})</div>
            <ul className="p-3 list-disc list-inside overflow-y-auto max-h-[85%]">
              {tachesNonTerminees.map((t, i) => (
                <li key={i}>{t.nom}</li>
              ))}
            </ul>
          </div>

          {/* Résumé */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white w-[85%] h-[90%] border flex flex-col justify-center items-center">
            <div className="w-full p-3 text-lg text-center border-b border-white">Résumés</div>
            <div className="p-6 text-xl text-center">
              <p>Total : {taches.length}</p>
              <p className="mt-2 text-green-400">✔️ Terminées : {tachesTerminees.length}</p>
              <p className="mt-2 text-red-400">❌ Non terminées : {tachesNonTerminees.length}</p>
            </div>
          </div>
        </div>

      </div>
    </div>
  );
}
