import { motion } from 'framer-motion';
import Kevin from '../assets/Kevin.JPG';
import { useState } from "react";

function Propos()
{
    const [SiAffiche, setSiAffiche] = useState(true)
    return(
        <div className="relative w-full h-screen transition-all duration-300">

                <div className="absolute top-0 flex items-center justify-center w-full h-20 text-3xl font-bold border-b shadow-md">À propos</div>
                <motion.div 
                    className="absolute flex items-center justify-center w-full h-full"
                    initial={{translateX:'100%'}}
                    animate={{translateX:0}}
                    transition={{duration:2}}
                >
                    {SiAffiche && 
                    <motion.div 
                        className={`w-1/2 text-white bg-gray-800 cursor-pointer h-1/2 transition-transform duration-500`}
                        onClick={()=>setSiAffiche(!SiAffiche)}
                        initial={{rotateX:0}}
                        whileTap={{opacity:0.5,rotateX:'360deg'}}
                        transition={{duration:2}}
                    >
                        <div className="p-4 text-2xl font-semibold text-center border border-b border-white">Gestion des tâches</div>
                        <div className="px-3 py-4 mt-16">L'application web pour la gestion des tâches est une application ayant pour but de faciliser la journée de chaque utilisateurs, et de ne pas oublier toutes les petites choses quotidienne.</div>
                        <div className="px-3 py-4">Si vous voulez voir le créateur, cliquez sur cette onglet ou sur le lien en dessous.</div>
                        <a href="https://niavo-kevin.netlify.app" className="px-3 py-5">Niavo Kevin</a>
                    </motion.div>}
                    {!SiAffiche && <div className="transition-transform duration-500 flex flex-col items-center w-[30%] text-white bg-gray-800 cursor-pointer h-1/2" onClick={()=>setSiAffiche(!SiAffiche)}>
                        <div className="p-6 overflow-hidden rounded-full w-80 h-80"><img className='object-cover' src={Kevin} alt="Kevin" drag /></div>
                        <div className="p-4 text-2xl font-semibold">MAMINIRINA Niavo Kevin</div>
                        <div className="flex ">
                            <a href="https://niavo-kevin.netlify.app" className="text-white cursor-pointer"><div className="px-3"><i className="text-4xl fab fa-whatsapp"></i></div></a>
                            <a href="https://niavo-kevin.netlify.app" className="text-white cursor-pointer"><div className="px-3"><i className="text-4xl fas fa-globe"></i></div></a>
                            <a href="https://niavo-kevin.netlify.app" className="text-white cursor-pointer"><div className="px-3"><i className="text-4xl fab fa-linkedin-in"></i></div></a>
                        </div>
                    </div>}
                </motion.div>
                <div className="absolute bottom-0 flex items-center justify-center w-full h-16 text-2xl font-semibold border-" style={{boxShadow: '-1px -2px 15px -10px rgba(0,0,0,0.23)'}}>&copy; STILL Development</div>

        </div>
    );
}
export default Propos;
