import { useState } from 'react'
function Sidebars () {
  // Etat
  const [SiOuvert, setSiOuvert] = useState(true)
  return (
    <div
      className={`h-screen bg-blue-200 relative transition-all duration-300 ${
        SiOuvert ? 'w-[20%]' : 'w-[5%]'
      }`}

    >
      <div>
        <div className={`text-center underline underline-offset-4 p-3 transition-all duration-300 ${
          SiOuvert ? 'text-3xl' : 'text-xl'
        }`}
        onClick={()=>setSiOuvert(true)}
        >
            {SiOuvert ? <div className=''>Menu</div> : <i className="fas fa-bars p-2 text-5xl"></i>}
        </div>
        <span className='absolute right-3 top-5' onClick={() => setSiOuvert(false)}>{SiOuvert && <i className='fas fa-angle-left text-3xl'></i>}</span>
      </div>
    </div>
  )
}
export default Sidebars
