import { useEffect, useState } from "react";
import axios from 'axios';
export default function TacheNonFini() {
    const [ Taches, setTaches] = useState([]);

    const AffichageTache = async () =>
    {
        const res = await axios.get('http://localhost:5000/tache');
        setTaches(res.data);
    }

    const SuppressionTache = async (id) =>
    {
        await axios.delete(`http://localhost:5000/tache/${id}`);
        setTaches(Taches.filter(tache => tache.id !== id));
    }
    useEffect(() =>{
        AffichageTache();
    },[]);

    return (
      <div className='w-full h-screen flex transition-all duration-300'>
        <div className='flex flex-col w-full transition-all duration-300 h-screen'>

          <div className="w-full h-20 text-center font-bold text-3xl border-b shadow-md flex items-center justify-center">
            Listes des t√¢ches non fini
          </div>

          <div className="p-6 w-full overflow-y-auto">
            <div className="rounded-lg shadow-md">
              <table className="min-w-full text-sm text-left text-gray-200 bg-gray-800 border border-gray-700 table-fixed">
                <thead className="text-xs uppercase bg-gray-900 text-gray-300">
                  <tr className="border-b border-white border-double">
                    <th className="px-5 py-3 text-xl text-center w-[15%]">Nom</th>
                    <th className="px-5 py-3 text-xl w-[45%]">Description</th>
                    <th className="px-5 py-3 text-xl text-center w-[20%]">Date</th>
                    <th className="px-5 py-3 text-xl text-center w-[20%]">Statut</th>
                    <th className="px-5 py-3 text-xl text-center w-[20%]">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {Taches.map((tache) => (
                    <tr key={tache.id} className={`border-b border-white hover:border-dashed cursor-pointer ${tache.id % 2 === 0 ? 'bg-gray-800' : 'bg-gray-700'}`}>
                        <td className="px-4 py-4 font-medium text-white w-[15%] break-words whitespace-normal text-center">{tache.nom}</td>
                        <td className="px-4 py-4 w-[30%] break-words whitespace-normal">{tache.description}</td>
                        <td className="px-4 py-4 w-[20%] break-words whitespace-normal text-center">{tache.date}</td>
                        <td className="px-4 py-4 w-[20%] break-words whitespace-normal text-center">{tache.status === 1 ? (<i className="fas fa-check text-green-500"></i>) : (<i className="fas fa-xmark text-red-500"></i>)}</td>
                        <td className="px-4 py-4 w-[15%] break-words whitespace-normal text-center">
                            <i className="fas fa-pen-to-square text-2xl mr-5 text-blue-500 cursor-pointer hover:text-blue-400"></i>
                            <i className="fas fa-trash-can text-2xl text-red-500 cursor-pointer hover:text-red-400" onClick={()=>SuppressionTache(tache.id)}></i>
                        </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

        </div>
      </div>
    );
  }
