import { useEffect, useState } from "react";
import axios from 'axios';
export default function TacheNonFini() {
    const [ Taches, setTaches] = useState([]);
    const [SelectTaches, SetSelectTaches] = useState("");

    const AffichageTache = async () =>
    {
        const res = await axios.get('http://localhost:5000/tache');
        setTaches(res.data);
    }

    const SuppressionTache = async (id) =>
    {
        await axios.delete(`http://localhost:5000/tache/${id}`);
        setTaches(Taches.filter(tache => tache.id !== id));
    }

    const AffichageTacheModif = async (id) =>
    {
        const res = await axios.get(`http://localhost:5000/tache/modifier/${id}`);
        SetSelectTaches(res.data)
    }

    const ModifierTache = async () => {
      await axios.put(`http://localhost:5000/tache/${SelectTaches.id}`, {
          nom: SelectTaches.nom,
          description: SelectTaches.description
      });

      AffichageTache(); // Recharger la liste
  }

    useEffect(() =>{
        AffichageTache();
    },[]);

    return (
      <div className='flex w-full h-screen transition-all duration-300'>
        <div className='flex flex-col w-full h-screen transition-all duration-300'>

          <div className="flex items-center justify-center w-full h-20 text-3xl font-bold text-center border-b shadow-md">
            Listes des tâches non fini
          </div>

          <div className="w-full p-6 overflow-y-auto">
            <div className="rounded-lg shadow-md">
              <table className="min-w-full text-sm text-left text-gray-200 bg-gray-800 border border-gray-700 table-fixed">
                <thead className="text-xs text-gray-300 uppercase bg-gray-800">
                  <tr className="border-b border-white border-double">
                    <th className="px-5 py-3 text-xl text-center w-[15%]">Nom</th>
                    <th className="px-5 py-3 text-xl w-[45%]">Description</th>
                    <th className="px-5 py-3 text-xl text-center w-[20%]">Date</th>
                    <th className="px-5 py-3 text-xl text-center w-[20%]">Statut</th>
                    <th className="px-5 py-3 text-xl text-center w-[20%]">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {Taches.map((tache) => (
                    <tr key={tache.id} className={`border-b border-white hover:border-dashed cursor-pointer ${tache.id % 2 === 0 ? 'bg-gray-900' : 'bg-gray-800'}`}>
                        <td className="px-4 py-4 font-medium text-white w-[15%] break-words whitespace-normal text-center">{tache.nom}</td>
                        <td className="px-4 py-4 w-[30%] break-words whitespace-normal">{tache.description}</td>
                        <td className="px-4 py-4 w-[20%] break-words whitespace-normal text-center">{tache.date}</td>
                        <td className="px-4 py-4 w-[20%] break-words whitespace-normal text-center">{tache.status === 1 ? (<i className="text-green-500 fas fa-check"></i>) : (<i className="text-red-500 fas fa-xmark"></i>)}</td>
                        <td className="px-4 py-4 w-[15%] break-words whitespace-normal text-center">
                            <i className="mr-5 text-2xl text-blue-500 cursor-pointer fas fa-pen-to-square hover:text-blue-400" data-bs-target='#modalmodifier' data-bs-toggle='modal' onClick={()=>AffichageTacheModif(tache.id)}></i>
                            <i className="text-2xl text-red-500 cursor-pointer fas fa-trash-can hover:text-red-400" onClick={()=>SuppressionTache(tache.id)}></i>
                        </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

        </div>

        <div className="modal fade" id="modalmodifier">
            <div className="modal-dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <div className="modal-title">Modifier une tâche</div>
                        <div className="btn-close" data-bs-dismiss='modal'></div>
                    </div>
                    <div className="modal-body">
                    <div className="mb-3">
                          <label className="form-label">Nom</label>
                          <input
                            type="text"
                            className="form-control"
                            value={SelectTaches.nom || ''}
                            onChange={(e) => SetSelectTaches({ ...SelectTaches, nom: e.target.value })}
                          />


                      </div>
                      <div className="mb-3">
                          <label className="form-label">Description</label>
                          <textarea
                            className="form-control"
                            value={SelectTaches.description || ''}
                            onChange={(e) => SetSelectTaches({ ...SelectTaches, description: e.target.value })}
                          />
                      </div>
                    </div>
                    <div className="modal-footer">
                        <button className="btn btn-danger" data-bs-dismiss='modal'>Annuler</button>
                        <button className="btn btn-primary" onClick={ModifierTache}>Modifier</button>
                    </div>
                </div>
            </div>
        </div>

      </div>
    );
  }
