import { useEffect, useState } from "react";
import axios from "axios";

export default function Index() {
  const [taches, setTaches] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/tache")
      .then(res => setTaches(res.data))
      .catch(err => console.error("Erreur lors du chargement des tâches", err));
  }, []);

  const tachesTerminees = taches.filter(t => t.status === 1);
  const tachesNonTerminees = taches.filter(t => t.status === 0);

  const toggleStatus = async (id, currentStatus) => {
    try {
      const newStatus = currentStatus === 1 ? 0 : 1;
      await axios.put(`http://localhost:5000/tache/${id}`, { status: newStatus });
      setTaches(taches.map(t =>
        t.id === id ? { ...t, status: newStatus } : t
      ));
    } catch (err) {
      console.error("Erreur lors du changement de statut", err);
    }
  };

  const renderTable = (Taches) => (
    <div className="overflow-auto h-[calc(100%-2.5rem)] mt-2">
      <table className="min-w-full text-xs text-left text-gray-200 bg-gray-800 border border-gray-700 table-fixed">
        <thead className="text-[10px] text-gray-300 uppercase bg-gray-800">
          <tr className="border-b border-white border-double">
            <th className="px-3 py-2 text-center w-[15%]">Nom</th>
            <th className="px-3 py-2 w-[40%]">Description</th>
            <th className="px-3 py-2 text-center w-[20%]">Date</th>
            <th className="px-3 py-2 text-center w-[10%]">Statut</th>
            <th className="px-3 py-2 text-center w-[15%]">Action</th>
          </tr>
        </thead>
        <tbody>
          {Taches.map((tache) => (
            <tr key={tache.id} className={`border-b ${tache.id % 2 === 0 ? 'bg-gray-900' : 'bg-gray-800'}`}>
              <td className="px-2 py-2 text-center break-words">{tache.nom}</td>
              <td className="px-2 py-2 break-words">{tache.description}</td>
              <td className="px-2 py-2 text-center">{new Date(tache.date).toLocaleDateString("fr-FR")}</td>
              <td className="px-2 py-2 text-center">
                {tache.status === 1 ? (
                  <i className="text-green-500 fas fa-check"></i>
                ) : (
                  <i className="text-red-500 fas fa-xmark"></i>
                )}
              </td>
              <td className="px-2 py-2 text-center">
                <button
                  onClick={() => toggleStatus(tache.id, tache.status)}
                  className="px-1 py-[2px] text-[10px] text-white bg-blue-600 rounded hover:bg-blue-700"
                >
                  Changer
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className='flex w-full h-screen overflow-hidden transition-all duration-300'>
      <div className='flex flex-col w-full h-screen transition-all duration-300'>

        <div className="relative flex items-center justify-center w-full h-20 text-2xl font-bold text-center border-b shadow-md">
          Dashboard
        </div>

        <div className="grid w-full h-full grid-cols-1 gap-5 px-5 py-4 overflow-auto md:grid-cols-2">

          {/* Liste des tâches */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white h-[85vh] border p-4 flex flex-col">
            <div className="pb-2 text-sm font-semibold text-center border-b border-white">
              Liste des tâches ({taches.length})
            </div>
            {renderTable(taches)}
          </div>

          {/* Tâches terminées */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white h-[85vh] border p-4 flex flex-col">
            <div className="pb-2 text-sm font-semibold text-center border-b border-white">
              Tâches terminées ({tachesTerminees.length})
            </div>
            {renderTable(tachesTerminees)}
          </div>

          {/* Tâches non terminées */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white h-[85vh] border p-4 flex flex-col">
            <div className="pb-2 text-sm font-semibold text-center border-b border-white">
              Tâches non terminées ({tachesNonTerminees.length})
            </div>
            {renderTable(tachesNonTerminees)}
          </div>

          {/* Résumé */}
          <div className="bg-gray-800 shadow-md rounded-lg text-white h-[85vh] border flex flex-col justify-center items-center p-4">
            <div className="w-full pb-2 text-sm font-semibold text-center border-b border-white">
              Résumés
            </div>
            <div className="p-6 mt-4 text-base text-center">
              <p>Total : {taches.length}</p>
              <p className="mt-2 text-green-400">✔️ Terminées : {tachesTerminees.length}</p>
              <p className="mt-2 text-red-400">❌ Non terminées : {tachesNonTerminees.length}</p>
            </div>
          </div>

        </div>
      </div>
    </div>
  );
}
