import { useState } from "react";

export default function App() {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="w-full h-screen flex transition-all duration-300">
      {/* Sidebar */}
      <div
        className={`bg-blue-200 h-full cursor-pointer transition-all duration-300 ${
          isOpen ? "w-[20%]" : "w-[5%]"
        }`}
        onClick={() => setIsOpen(!isOpen)}
      >
        {isOpen ? "Sidebar (Cliquez pour réduire)" : ">>"}
      </div>

      {/* Contenu principal */}
      <div
        className={`bg-red-300 h-full transition-all duration-300 ${
          isOpen ? "w-[80%]" : "w-[95%]"
        }`}
      >
        Tout les autres contenus
      </div>
    </div>
  );
}
